mapper.select:
SELECT stock_date, open_price, high_price, low_price, close_price FROM stock_data
WHERE stock_code=#{stock_code} AND stock_date 
	BETWEEN DATE_FORMAT(DATE_ADD(NOW(), INTERVAL -3 MONTH), "%Y%m%d") AND DATE_FORMAT(DATE_ADD(NOW(), INTERVAL -2 MONTH), "%Y%m%d");

mapper.update:
UPDATE stock_list
SET view_cnt = view_cnt + 1
WHERE stock_code = #{stock_code}
